Array3 applyColormap(float value, const std::string &cmapKey) {
	value = std::clamp(value, 0.0f, 1.0f);
	if (cmaps.count(cmapKey) > 0) {
		int i = value * 255;
		return Array3(cmaps[cmapKey][i][0], cmaps[cmapKey][i][1], cmaps[cmapKey][i][2]);
	}
	return Array3(value, value, value);
}

std::shared_ptr<Image<3>> getColormappedImage(std::shared_ptr<Image<3>>& image,
											  const std::string colormap,
											  float minVal = 0, float maxVal = 1) {
	std::shared_ptr<Image<3>> color = std::make_shared<Image<3>>(image->h, image->w);
	for (int i = 0; i < image->h; i++) {
		for (int j = 0; j < image->w; j++) {
			float val = std::clamp((image->get(i, j)[0] - minVal)/(maxVal - minVal), 0.0f, 1.0f);
			color->get(i, j) = applyColormap(val, colormap);
		}
	}
	return color;
}
